(function(){var app=angular.module("app");app.directive("onlyNumbers",function(){return{restrict:"A",link:function(scope,elm,attrs,ctrl){elm.on("keydown",function(event){console.log(event.which);if(event.shiftKey){event.preventDefault();return false}if([8,13,27,37,38,39,40].indexOf(event.which)>-1){return true}else if(event.which>=48&&event.which<=57){return true}else if(event.which>=95&&event.which<=105){return true}else{event.preventDefault();return false}})}}});app.directive("noFloat",function(){return{restrict:"A",link:function(scope,elm,attrs,ctrl){elm.on("keydown",function(event){if([110,190].indexOf(event.which)>-1){event.preventDefault();return false}else{return true}})}}})})();